CREATE OR REPLACE VIEW RECETTEMONTANT AS
select
    rec.ID,rec.IDPRODUITS,rec.IDINGREDIENTS,cast(rec.QUANTITE as number(30,10)) as QUANTITE,rec.UNITE,'-' as libIngredients,'-' as IDUNITE,'' as source,0 as COMPOSE,
    cast(0 as number(30,10)) as qteav,
    cast(0 as number(30,10)) as qtetotal,
    i.TYPESTOCK,
    i.REFPOST,
    i.REFQUALIFICATION,
    i.CATEGORIEINGREDIENT ,
    0 as niveau
from as_recette rec
         left join AS_INGREDIENTS i on rec.IDINGREDIENTS=i.ID
;


CREATE OR REPLACE VIEW AS_RECETTECOMPOSE AS
select r.ID,
       r.IDPRODUITS,
       r.IDINGREDIENTS,
       CAST(r.QUANTITE AS NUMBER(30,
                                 10)) AS quantite,
       nvl(r.UNITE, u.VAL) as unite,
       ing.compose,
       ing.TYPESTOCK,
       ing.CATEGORIEINGREDIENT
FROM
    as_recette r,
    as_ingredients ing,
    as_unite u
WHERE
    r.IDINGREDIENTS = ing.id(+)
  and ing.UNITE=u.id;





